//import React, { useState } from 'react';
import React from 'react';
import './waterniveau.styles.scss';
import firebase from '../../firebase';

class Waterniveau extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            //status: null, //okay, filling or something else
            boodschappen: [],
            stopzetten: "",
            bijvullen:"",
        }
    }
    componentDidMount() {
        /*var items = [];
        firebase
        .firestore()
        .collection("boodschap")
        .get()
        .then((querySnapshot) => {  //Notice the arrow funtion which bind `this` automatically.
            querySnapshot.forEach(function(doc) {
                items.push(doc.data());
            });
            this.setState({ items: items });  
            if(this.state.items[0].manueleAanvraag || this.state.items[0].manueleStop || this.state.items[0].manueleAanvraag === false || this.state.items[0].manueleStop === false){
                this.setState({status: "okay"})
            } else {
                this.setState({status: "not okay"})
            }
            //this.setState({verlichting: this.state.items[0].aan})
            console.log(this.state.items[0].manueleAanvraag)
            //set data in state here
        });*/

        var boodschappen = [];
        firebase
        .firestore()
        .collection("boodschap")
        .get()
        .then((querySnapshot) => {  //Notice the arrow funtion which bind `this` automatically.
            querySnapshot.forEach(function(doc) {
                boodschappen.push(doc.data());
            });
            this.setState({ boodschappen: boodschappen });  
            this.setState({stopzetten: this.state.boodschappen[0].manueleStop})
            this.setState({bijvullen: this.state.boodschappen[0].manueleAanvraag})
            //set data in state here
        });


    }

    render(){
        //make de error pinking
        const {stopzetten} = this.state;
        console.log(this.state);

        return(
            <div>
                    {stopzetten === false ? 
                        <div className='waterniveau green'>
                            <div className="title">WATERTOEVOER</div>
                            <div className="status">STATUS: Systeem staat aan</div>
                        </div>
                    : 
                        <div className='waterniveau red'>
                            <div className="title">WATERTOEVOER</div>
                            <div className="statuswhite">STATUS: Systeem staat uit </div>

                        </div> 
                    }

            </div>


        );
    }
}

export default Waterniveau;
